import React, { useState } from "react";
import Avatar from "./components/avatar";
import "./index.css";

const App = () => {
  const [users, setUser] = useState([]);
  const loadUsers = async () => {
    // console.log("before");
    const response = await fetch("https://api.github.com/users");
    const jsonresponse = await response.json();
    setUser(jsonresponse);
  };

  return (
    <div className="App">
      <Nav>
        <Bars />

        <NavMenu>
          <NavLink to='/about' activeStyle>
            About
          </NavLink>
          <NavLink to='/events' activeStyle>
            Events
          </NavLink>
          <NavLink to='/annual' activeStyle>
            Annual Report
          </NavLink>
          <NavLink to='/team' activeStyle>
            Teams
          </NavLink>
          <NavLink to='/blogs' activeStyle>
            Blogs
          </NavLink>
          <NavLink to='/sign-up' activeStyle>
            Sign Up
          </NavLink>
          {/* Second Nav */}
          {/* <NavBtnLink to='/sign-in'>Sign In</NavBtnLink> */}
        </NavMenu>
        <NavBtn>
          <NavBtnLink to='/signin'>Sign In</NavBtnLink>
        </NavBtn>
      </Nav>
      <h1>ZIPPY Users</h1>
      <h2>Hello All, Click Get users button to get user and their details</h2>
      <button className="btn-user" onClick={loadUsers}>Get Users</button>
      <h2>Users will appear below:</h2>


        <div className = "user-row">
        {users.map(({ id, login, avatar_url }) => {
          // <li key={id}>
          //   Name:{login}
          //   Avator: {avatar_url}
          // </li>
                      return (
                        // <header className = "navbar">
                        //   {/* <h1>ZIPPY-Users</h1>
                        //   <div className="btn" onClick={loadUsers}>
                        //     GET USERS
                        //   </div> */}
                        // </header>
              <div className="user-card user-column" key={id}>
                <img src={avatar_url} className="uppercard" alt="..." />
                <div className="card-body">
                  <h3 className="title-card-user">
                    {login}
                  </h3>
                  <h4 className="user-text"></h4>
                </div>
              </div>
            );

                      })}
      </div>
      </div>

  );
};
export default App;
